function transform(rawData,lodash, moment) {
    // for demonstration purpose: how to use 'moment'
    // const t = moment(new Date()).format("YY-MM-DD HH:mm::ss")
    const d = rawData.flat()
    const data = []
    for (const s of d) {
        const version = s.fields.find(f => f.type == "number").labels.version
        const series = {
            name: s.name,
            fields: [{
                type: "string",
                name: "version",
                values:[version]
            }]
        }
        data.push(series)
    }
    return data.length > 0 ? [data] : []
}

