panels.transform:
function transform(rawData, lodash, moment) {
    console.log("here33333:", rawData)
    // for demonstration purpose: how to use 'moment'
    // const t = moment(new Date()).format("YY-MM-DD HH:mm::ss")
    const data = []
    const series = {
        name: "disk avaliable spaces",
        fields: [
            {
                name: "Disk",
                values: []
            },
            {
                name: "Mount",
                values: []
            },
            {
                "name": "Total",
                values: []
            },
            {
                "name": "Available",
                values: []
            }
        ]
    }

    for (const s of rawData.flat()) {
        if (s.fields[1].labels.__name__ == "node_filesystem_avail_bytes") {
            series.fields[0].values.push(s.fields[1].labels.device)
            series.fields[1].values.push(s.fields[1].labels.mountpoint)
            series.fields[3].values.push(lodash.last(s.fields[1].values))
        } else if (s.fields[1].labels.__name__ == "node_filesystem_size_bytes") {
            series.fields[2].values.push(lodash.last(s.fields[1].values))
        }

    }

    data.push(series)

    return [data]
}

