value.transform:
function transform(data, lodash){
    const used = data.value
    const freeValues = data.data.find(s => s.name == "Free").fields.find(f => f.type == "number").values

    const free = data.calcValueOnArray(freeValues, data.calc)
    return used / (used + free)
}

